import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';
import { CartService } from '../../services/cart.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {
  loginForm: FormGroup;
  isSubmitting = false;
  error: string | null = null;
  returnUrl: string = '/';

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private cartService: CartService,
    private route: ActivatedRoute,
    private router: Router
  ) {
    // Initialize form
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });

    // Redirect to home if already logged in
    if (this.authService.isAuthenticated()) {
      this.router.navigate(['/']);
    }
  }

  ngOnInit(): void {
    // Get return url from route parameters or default to '/'
    this.returnUrl = this.route.snapshot.queryParams['returnUrl'] || '/';
  }

  onSubmit(): void {
    if (this.loginForm.invalid) {
      return;
    }

    this.isSubmitting = true;
    this.error = null;

    const { email, password } = this.loginForm.value;

    this.authService.login(email, password).subscribe({
      next: () => {
        this.isSubmitting = false;

        // Load user's cart
        this.cartService.loadCart();

        // Navigate to return url
        this.router.navigateByUrl(this.returnUrl);
      },
      error: (error) => {
        this.isSubmitting = false;
        this.error = error.message || 'Invalid email or password. Please try again.';
      }
    });
  }

  // Form getters for validation
  get f() { return this.loginForm.controls; }
}


























































































































































































































































































































































































<div class="auth-page">
  <div class="auth-card">
    <div class="auth-header">
      <h1>Login</h1>
      <p>Welcome back! Please login to your account.</p>
    </div>

    <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
      <div *ngIf="error" class="alert alert-danger">
        {{ error }}
      </div>

      <div class="form-group">
        <label for="email">Email</label>
        <input 
          type="email" 
          id="email" 
          formControlName="email" 
          class="form-control" 
          [class.is-invalid]="f.email.invalid && f.email.touched">
        <div *ngIf="f.email.invalid && f.email.touched" class="invalid-feedback">
          <div *ngIf="f.email.errors?.required">Email is required</div>
          <div *ngIf="f.email.errors?.email">Please enter a valid email address</div>
        </div>
      </div>

      <div class="form-group">
        <label for="password">Password</label>
        <input 
          type="password" 
          id="password" 
          formControlName="password" 
          class="form-control" 
          [class.is-invalid]="f.password.invalid && f.password.touched">
        <div *ngIf="f.password.invalid && f.password.touched" class="invalid-feedback">
          <div *ngIf="f.password.errors?.required">Password is required</div>
          <div *ngIf="f.password.errors?.minlength">Password must be at least 6 characters</div>
        </div>
      </div>

      <div class="form-group form-check">
        <input type="checkbox" class="form-check-input" id="rememberMe">
        <label class="form-check-label" for="rememberMe">Remember me</label>
        <a routerLink="/forgot-password" class="forgot-password">Forgot password?</a>
      </div>

      <button 
        type="submit" 
        class="btn btn-primary btn-block" 
        [disabled]="loginForm.invalid || isSubmitting">
        <span *ngIf="!isSubmitting">Login</span>
        <span *ngIf="isSubmitting">Logging in...</span>
      </button>
    </form>

    <div class="auth-footer">
      <p>Don't have an account? <a routerLink="/register" [queryParams]="{returnUrl: returnUrl}">Register</a></p>
    </div>
  </div>
</div>
