  <div class="auth-card">
    <div class="auth-header">
      <h1>Create Account</h1>
      <p>Sign up to start shopping with us!</p>
    </div>

    <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
      <div *ngIf="error" class="alert alert-danger">
        {{ error }}
      </div>

      <div class="form-row">
        <div class="form-group col-md-6">
          <label for="firstName">First Name</label>
          <input 
            type="text" 
            id="firstName" 
            formControlName="firstName" 
            class="form-control" 
            [class.is-invalid]="f['firstName'].invalid && f['firstName'].touched">
          <div *ngIf="f['firstName'].invalid && f['firstName'].touched" class="invalid-feedback">
            First name is required
          </div>
        </div>

        <div class="form-group col-md-6">
          <label for="lastName">Last Name</label>
          <input 
            type="text" 
            id="lastName" 
            formControlName="lastName" 
            class="form-control" 
            [class.is-invalid]="f['lastName'].invalid && f['lastName'].touched">
          <div *ngIf="f['lastName'].invalid && f['lastName'].touched" class="invalid-feedback">
            Last name is required
          </div>
        </div>
      </div>

      <div class="form-group">
        <label for="email">Email</label>
        <input 
          type="email" 
          id="email" 
          formControlName="email" 
          class="form-control" 
          [class.is-invalid]="f['email'].invalid && f['email'].touched">
        <div *ngIf="f['email'].invalid && f['email'].touched" class="invalid-feedback">
          <div *ngIf="f['email'].errors?.['required']">Email is required</div>
          <div *ngIf="f['email'].errors?.['email']">Please enter a valid email address</div>
        </div>
      </div>

      <div class="form-group">
        <label for="password">Password</label>
        <input 
          type="password" 
          id="password" 
          formControlName="password" 
          class="form-control" 
          [class.is-invalid]="f['password'].invalid && f['password'].touched">
        <div *ngIf="f['password'].invalid && f['password'].touched" class="invalid-feedback">
          <div *ngIf="f['password'].errors?.['required']">Password is required</div>
          <div *ngIf="f['password'].errors?.['minlength']">Password must be at least 6 characters</div>
        </div>
      </div>

      <div class="form-group">
        <label for="confirmPassword">Confirm Password</label>
        <input 
          type="password" 
          id="confirmPassword" 
          formControlName="confirmPassword" 
          class="form-control" 
          [class.is-invalid]="f['confirmPassword'].invalid && f['confirmPassword'].touched">
        <div *ngIf="f['confirmPassword'].invalid && f['confirmPassword'].touched" class="invalid-feedback">
          <div *ngIf="f['confirmPassword'].errors?.['required']">Please confirm your password</div>
          <div *ngIf="f['confirmPassword'].errors?.['passwordMismatch']">Passwords do not match</div>
        </div>
      </div>

      <div class="form-group form-check">
        <input type="checkbox" class="form-check-input" id="termsAgreed">
        <label class="form-check-label" for="termsAgreed">
          I agree to the <a href="#">Terms of Service</a> and <a href="#">Privacy Policy</a>
        </label>
      </div>

      <button 
        type="submit" 
        class="btn btn-primary btn-block" 
        [disabled]="registerForm.invalid || isSubmitting">
        <span *ngIf="!isSubmitting">Create Account</span>
        <span *ngIf="isSubmitting">Creating Account...</span>
      </button>
    </form>

    <div class="auth-footer">
      <p>Already have an account? <a routerLink="/login" [queryParams]="{returnUrl: returnUrl}">Login</a></p>
    </div>
  </div>
</div>
