import { Component, OnInit } from '@angular/core';
import { Product } from '../../models/product.model';
import { Category } from '../../models/category.model';
import { ProductService } from '../../services/product.service';
import { CategoryService } from '../../services/category.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  featuredProducts: Product[] = [];
  categories: Category[] = [];
  isLoading = true;
  error: string | null = null;

  constructor(
    private productService: ProductService,
    private categoryService: CategoryService
  ) { }

  ngOnInit(): void {
    this.loadFeaturedProducts();
    this.loadCategories();
  }

  loadFeaturedProducts(): void {
    this.productService.getProducts().subscribe({
      next: (products) => {
        // In a real app, you might have a featured flag or use a specific endpoint
        this.featuredProducts = products.slice(0, 8); // Just take first 8 for demo
        this.isLoading = false;
      },
      error: (error) => {
        console.error('Error loading featured products:', error);
        this.error = 'Failed to load featured products. Please try again later.';
        this.isLoading = false;
      }
    });
  }

  loadCategories(): void {
    this.categoryService.getCategories().subscribe({
      next: (categories) => {
        this.categories = categories;
      },
      error: (error) => {
        console.error('Error loading categories:', error);
      }
    });
  }

  onProductAddedToCart(product: Product): void {
    // You could show a notification or update UI here
    console.log(`Product added to cart: ${product.name}`);
  }
}



























































































































































































































































































<section class="hero">
  <div class="hero-content">
    <h1>Welcome to ShopCart</h1>
    <p>Your one-stop shop for all your shopping needs</p>
    <div class="hero-buttons">
      <a routerLink="/products" class="btn btn-primary">Shop Now</a>
      <a routerLink="/categories" class="btn btn-outline">Browse Categories</a>
    </div>
  </div>
</section>

<section class="featured-products">
  <div class="section-header">
    <h2>Featured Products</h2>
    <a routerLink="/products" class="view-all">View All <i class="fas fa-arrow-right"></i></a>
  </div>

  <div *ngIf="isLoading" class="loading-container">
    <div class="spinner"></div>
    <p>Loading products...</p>
  </div>

  <div *ngIf="error" class="error-container">
    <i class="fas fa-exclamation-circle"></i>
    <p>{{ error }}</p>
    <button class="btn btn-primary" (click)="loadFeaturedProducts()">Try Again</button>
  </div>

  <div *ngIf="!isLoading && !error" class="products-grid">
    <div *ngFor="let product of featuredProducts" class="product-item">
      <app-product-card 
        [product]="product"
        (addedToCart)="onProductAddedToCart($event)">
      </app-product-card>
    </div>
  </div>
</section>

<section class="categories">
  <div class="section-header">
    <h2>Shop by Category</h2>
  </div>

  <div class="categories-grid">
    <div *ngFor="let category of categories" class="category-item">
      <a [routerLink]="['/products']" [queryParams]="{category: category.categoryId}">
        <div class="category-card">
          <h3>{{ category.name }}</h3>
          <p>{{ category.description }}</p>
          <span class="browse-link">Browse <i class="fas fa-arrow-right"></i></span>
        </div>
      </a>
    </div>
  </div>
</section>

<section class="cta">
  <div class="cta-content">
    <h2>Join Our Newsletter</h2>
    <p>Subscribe to get updates on new products and special offers</p>

    <form class="newsletter-form">
      <div class="form-group">
        <input type="email" class="form-control" placeholder="Your email address">
        <button type="submit" class="btn btn-primary">Subscribe</button>
      </div>
    </form>
  </div>
</section>
