import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Product } from '../../models/product.model';
import { Category } from '../../models/category.model';
import { ProductService } from '../../services/product.service';
import { CategoryService } from '../../services/category.service';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.scss']
})
export class ProductListComponent implements OnInit {
  products: Product[] = [];
  categories: Category[] = [];
  filteredProducts: Product[] = [];
  selectedCategory: string | null = null;
  searchQuery: string = '';
  sortOption: string = 'name_asc';
  isLoading = true;
  error: string | null = null;

  constructor(
    private productService: ProductService,
    private categoryService: CategoryService,
    private route: ActivatedRoute,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.loadCategories();

    // Subscribe to query params to get filters
    this.route.queryParams.subscribe(params => {
      this.selectedCategory = params['category'] || null;
      this.searchQuery = params['search'] || '';
      this.sortOption = params['sort'] || 'name_asc';

      this.loadProducts();
    });
  }

  loadProducts(): void {
    this.isLoading = true;

    // If category filter is applied
    if (this.selectedCategory) {
      this.productService.getProductsByCategory(this.selectedCategory).subscribe({
        next: (products) => {
          this.products = products;
          this.applyFilters();
          this.isLoading = false;
        },
        error: (error) => {
          console.error('Error loading products by category:', error);
          this.error = 'Failed to load products. Please try again later.';
          this.isLoading = false;
        }
      });
    } 
    // If search query is applied
    else if (this.searchQuery) {
      this.productService.searchProducts(this.searchQuery).subscribe({
        next: (products) => {
          this.products = products;
          this.applyFilters();
          this.isLoading = false;
        },
        error: (error) => {
          console.error('Error searching products:', error);
          this.error = 'Failed to search products. Please try again later.';
          this.isLoading = false;
        }
      });
    } 
    // Get all products
    else {
      this.productService.getProducts().subscribe({
        next: (products) => {
          this.products = products;
          this.applyFilters();
          this.isLoading = false;
        },
        error: (error) => {
          console.error('Error loading products:', error);
          this.error = 'Failed to load products. Please try again later.';
          this.isLoading = false;
        }
      });
    }
  }

  loadCategories(): void {
    this.categoryService.getCategories().subscribe({
      next: (categories) => {
        this.categories = categories;
      },
      error: (error) => {
        console.error('Error loading categories:', error);
      }
    });
  }

  applyFilters(): void {
    // First apply any text search filter
    let filtered = [...this.products];

    if (this.searchQuery) {
      const query = this.searchQuery.toLowerCase();
      filtered = filtered.filter(product => 
        product.name.toLowerCase().includes(query) || 
        product.description.toLowerCase().includes(query)
      );
    }

    // Then sort the results
    switch (this.sortOption) {
      case 'name_asc':
        filtered.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case 'name_desc':
        filtered.sort((a, b) => b.name.localeCompare(a.name));
        break;
      case 'price_asc':
        filtered.sort((a, b) => a.price - b.price);
        break;
      case 'price_desc':
        filtered.sort((a, b) => b.price - a.price);
        break;
    }

    this.filteredProducts = filtered;
  }

  onCategoryChange(event: Event): void {
    const categoryId = (event.target as HTMLSelectElement).value || null;
    this.updateQueryParams({ category: categoryId });
  }

  onSortChange(event: Event): void {
    const sortOption = (event.target as HTMLSelectElement).value;
    this.updateQueryParams({ sort: sortOption });
  }

  onSearch(query: string): void {
    this.updateQueryParams({ search: query });
  }

  updateQueryParams(params: any): void {
    this.router.navigate([], {
      relativeTo: this.route,
      queryParams: { ...params },
      queryParamsHandling: 'merge'
    });
  }

  getCategoryName(categoryId: string): string {
    const category = this.categories.find(c => c.categoryId === categoryId);
    return category ? category.name : "";
  }

  onProductAddedToCart(product: Product): void {
    // You could show a notification or update UI here
    console.log(`Product added to cart: ${product.name}`);
  }

  clearFilters(): void {
    this.router.navigate(['/products']);
  }
}
























































































































































<div class="product-list-page">
  <div class="page-header">
    <h1>Products</h1>
    <p *ngIf="selectedCategory">Category: {{ getCategoryName(selectedCategory) }}</p>
    <p *ngIf="searchQuery">Search results for: "{{ searchQuery }}"</p>
  </div>

  <div class="filters-section">
    <div class="search-bar">
      <div class="form-group">
        <input 
          type="text" 
          class="form-control" 
          placeholder="Search products..." 
          [value]="searchQuery"
          #searchInput
          (keyup.enter)="onSearch(searchInput.value)">
        <button 
          class="search-button" 
          (click)="onSearch(searchInput.value)">
          <i class="fas fa-search"></i>
        </button>
      </div>
    </div>

    <div class="filter-options">
      <div class="form-group">
        <label for="category">Category</label>
        <select 
          id="category" 
          class="form-control" 
          [value]="selectedCategory || ''" 
          (change)="onCategoryChange($event)">
          <option value="">All Categories</option>
          <option *ngFor="let category of categories" [value]="category.categoryId">
            {{ category.name }}
          </option>
        </select>
      </div>

      <div class="form-group">
        <label for="sort">Sort By</label>
        <select 
          id="sort" 
          class="form-control" 
          [value]="sortOption"
          (change)="onSortChange($event)">
          <option value="name_asc">Name (A-Z)</option>
          <option value="name_desc">Name (Z-A)</option>
          <option value="price_asc">Price (Low to High)</option>
          <option value="price_desc">Price (High to Low)</option>
        </select>
      </div>

      <button 
        class="btn btn-outline clear-filters" 
        *ngIf="selectedCategory || searchQuery" 
        (click)="clearFilters()">
        Clear Filters
      </button>
    </div>
  </div>

  <div *ngIf="isLoading" class="loading-container">
    <div class="spinner"></div>
    <p>Loading products...</p>
  </div>

  <div *ngIf="error" class="error-container">
    <i class="fas fa-exclamation-circle"></i>
    <p>{{ error }}</p>
    <button class="btn btn-primary" (click)="loadProducts()">Try Again</button>
  </div>

  <div *ngIf="!isLoading && !error" class="products-container">
    <div *ngIf="filteredProducts.length === 0" class="no-products">
      <i class="fas fa-search"></i>
      <h3>No products found</h3>
      <p>Try adjusting your search or filter criteria</p>
      <button class="btn btn-primary" (click)="clearFilters()">Clear Filters</button>
    </div>

    <div *ngIf="filteredProducts.length > 0" class="products-grid">
      <div *ngFor="let product of filteredProducts" class="product-item">
        <app-product-card 
          [product]="product"
          (addedToCart)="onProductAddedToCart($event)">
        </app-product-card>
      </div>
    </div>
  </div>
</div>
