import { Component, Input, Output, EventEmitter } from '@angular/core';
import { CartItem } from '../../models/cart.model';
import { CartService } from '../../services/cart.service';

@Component({
  selector: 'app-cart-item',
  templateUrl: './cart-item.component.html',
  styleUrls: ['./cart-item.component.scss']
})
export class CartItemComponent {
  @Input() item!: CartItem;
  @Output() quantityChanged = new EventEmitter<CartItem>();
  @Output() itemRemoved = new EventEmitter<CartItem>();

  isUpdating = false;

  constructor(private cartService: CartService) { }

  updateQuantity(quantity: number): void {
    if (quantity < 1) return;

    this.isUpdating = true;

    this.cartService.updateCartItem(this.item.productId, quantity).subscribe({
      next: () => {
        this.isUpdating = false;
        this.quantityChanged.emit(this.item);
      },
      error: (error) => {
        console.error('Error updating cart item:', error);
        this.isUpdating = false;
      }
    });
  }

  removeItem(): void {
    this.isUpdating = true;

    this.cartService.removeFromCart(this.item.productId).subscribe({
      next: () => {
        this.isUpdating = false;
        this.itemRemoved.emit(this.item);
      },
      error: (error) => {
        console.error('Error removing cart item:', error);
        this.isUpdating = false;
      }
    });
  }

  incrementQuantity(): void {
    this.updateQuantity(this.item.quantity + 1);
  }

  decrementQuantity(): void {
    if (this.item.quantity > 1) {
      this.updateQuantity(this.item.quantity - 1);
    }
  }
}























































































































































































































































<div class="cart-item" [class.updating]="isUpdating">
  <div class="cart-item-image">
    <a [routerLink]="['/products', item.productId]">
      <img [src]="item.imageUrl" [alt]="item.name">
    </a>
  </div>

  <div class="cart-item-details">
    <h3 class="cart-item-name">
      <a [routerLink]="['/products', item.productId]">{{ item.name }}</a>
    </h3>

    <div class="cart-item-price">${{ item.price.toFixed(2) }}</div>

    <div class="cart-item-actions">
      <div class="quantity-control">
        <button 
          class="quantity-btn" 
          [disabled]="isUpdating || item.quantity <= 1" 
          (click)="decrementQuantity()">
          <i class="fas fa-minus"></i>
        </button>

        <span class="quantity">{{ item.quantity }}</span>

        <button 
          class="quantity-btn" 
          [disabled]="isUpdating" 
          (click)="incrementQuantity()">
          <i class="fas fa-plus"></i>
        </button>
      </div>

      <button 
        class="remove-btn" 
        [disabled]="isUpdating" 
        (click)="removeItem()">
        <i class="fas fa-trash"></i>
        <span>Remove</span>
      </button>
    </div>
  </div>

  <div class="cart-item-subtotal">
    <div class="subtotal-label">Subtotal:</div>
    <div class="subtotal-amount">${{ (item.price * item.quantity).toFixed(2) }}</div>
  </div>

  <div class="updating-overlay" *ngIf="isUpdating">
    <div class="spinner"></div>
  </div>
</div>
